import React, { useState } from 'react';
import axios from 'axios';
import './App.css';
import { FaCloudUploadAlt, FaVideo } from 'react-icons/fa';

function App() {
  const [video, setVideo] = useState(null);
  const [result, setResult] = useState("");
  const [loading, setLoading] = useState(false);

  const handleUpload = (e) => {
    setVideo(e.target.files[0]);
  };

  const handleSubmit = async () => {
    if (!video) {
      alert("üìÅ Please upload a video first!");
      return;
    }

    setLoading(true);
    const formData = new FormData();
    formData.append('video', video);

    try {
      const res = await axios.post('https://posture-pal-app-5.onrender.com/analyze', formData);
      setResult(res.data.feedback);
    } catch (err) {
      setResult(err?.response?.data?.feedback || "‚ùå Something went wrong.");
    }
    setLoading(false);
  };

  const getResultIcon = () => {
    if (result.includes("Bad") || result.includes("wrong") || result.includes("Unsupported")) {
      return "‚ùå";
    } else {
      return "‚úÖ";
    }
  };

  const getResultClass = () => {
    if (result.includes("Bad") || result.includes("wrong") || result.includes("Unsupported")) {
      return "bad";
    } else {
      return "good";
    }
  };

  return (
    <div className="app">
      <header className="header">
        <FaVideo className="header-icon" />
        <h1>Posture Pal üßç‚Äç‚ôÄÔ∏è</h1>
      </header>

      <div className="card">
        <label htmlFor="upload" className="upload-label">
          <FaCloudUploadAlt /> Upload a posture video/image
        </label>
        <input id="upload" type="file" accept="video/*,image/*" onChange={handleUpload} hidden />

        <button onClick={handleSubmit}>üîç Check Posture</button>

        {loading && <p className="loading">‚è≥ Analyzing posture...</p>}

        {!loading && result && (
          <p className={getResultClass()}>{getResultIcon()} {result}</p>
        )}
      </div>

      <footer className="footer">
        <p>Made with üíô by Pragya | 2025</p>
      </footer>
    </div>
  );
}

export default App;
